import React from 'react';
import { IAffilie } from '../../../app/models/affilie';
import { Document, Page, Text, View, StyleSheet} from '@react-pdf/renderer'
import { observer } from 'mobx-react-lite';
import {format} from 'date-fns'
import local from 'date-fns/locale/fr'

const PdfQp:  React.FC<{affilie: IAffilie }> = ({ affilie }) => {


    const styles = StyleSheet.create({
        page: {
            fontFamily: 'Helvetica',
            fontSize: 9,
            paddingTop: 20,
            paddingLeft:10,
            paddingRight:10,
            lineHeight: 1.5,
            flexDirection: 'column',
        }, 
        logo: {
            width: 74,
            height: 66,
            marginLeft: 'auto',
            marginRight: 'auto'
        },
        row: {
            flexDirection: 'row',
            borderBottomColor: '#bff0fd',
            borderBottomWidth: 1,
            alignItems: 'center',
            height: 24,
            fontStyle: 'bold',
        },
        description: {
            flexDirection: 'column',
            width: '20%',
            textAlign: 'center',
            borderRightColor: '#bff0fd',
            borderRightWidth: 1,
            paddingLeft: 8,
        },


        titleContainer:{
            flexDirection: 'row',
            marginTop: 24,
        },
        reportTitle:{
            color: '#61dafb',
            letterSpacing: 4,
            fontSize: 25,
            textAlign: 'center',
            textTransform: 'uppercase',
        }
      });




  return (
    <Document>
        <Page size="A4" style={styles.page}>

        <View style={styles.titleContainer}>
        <Text style={styles.reportTitle}>zzzzz</Text>
       </View>
            {affilie.Qps.map(qp=> 
               <View style={styles.row}>
                 <Text style={styles.description}>{qp.nDossier}</Text>
                <Text style={styles.description}>{qp.fraisEngage}</Text>
                <Text style={styles.description}>{qp.rembAmo} </Text>
                <Text style={styles.description}>{qp.rembMpsc} </Text>
                <Text style={styles.description}>{format(qp.datePaiementReel, 'dd/MM/yyyy', { locale: local })}</Text>
               </View>
                )}

        </Page>
      </Document>
  );


};

export default observer(PdfQp);
