import React, { useContext, useState, useEffect } from 'react';
import { Menu, Container, Button, Dropdown, Image, Input } from 'semantic-ui-react';
import { observer } from 'mobx-react-lite';
import { NavLink, Link } from 'react-router-dom';
import { RootStoreContext } from '../../app/stores/rootStore';

const NavBar: React.FC = () => {
  const rootStore = useContext(RootStoreContext);
  const { user, logout } = rootStore.userStore;
  const { setCin,loadAffilies,cin } = rootStore.affilieStore;
  const [loadingNext, setLoadingNext] = useState(false);

  const handleGetAffilie = () => {
    setLoadingNext(true);
    loadAffilies().then(() => setLoadingNext(false));
    console.log(cin)

  };



  return (
    <Menu fixed='top' inverted>
      <Container>
        <Menu.Item header as={NavLink} exact to='/'>
          <img src='/assets/logo.png' alt='logo' style={{ marginRight: 10 }} />
          Immatriculation
        </Menu.Item>
        <Menu.Item name='Affilies' as={NavLink} to='/affilies' />
        <Menu.Item>
          <Button
            as={NavLink}
            to='/createActivity'
            positive
            content='Create Affilie'
          />
        </Menu.Item>
        <Menu.Item>
        <Input placeholder='Search...' onChange={(e) => setCin(e.target.value) } onClick={() => handleGetAffilie() } />
        </Menu.Item>

        {user && (
          <Menu.Item position='right'>
            <Image avatar spaced='right' src={user.image || '/assets/user.png'} />
            <Dropdown pointing='top left' text={user.displayName}>
              <Dropdown.Menu>
                <Dropdown.Item
                  as={Link}
                  to={`/profile/${user.username}`}
                  text='My profile'
                  icon='user'
                />
                <Dropdown.Item onClick={logout} text='Logout' icon='power' />
              </Dropdown.Menu>
            </Dropdown>
          </Menu.Item>
        )}
      </Container>
    </Menu>
  );
};

export default observer(NavBar);
