import React, { Fragment, useContext, useEffect } from 'react';
import { Container } from 'semantic-ui-react';
import NavBar from '../../features/nav/NavBar';



import { observer } from 'mobx-react-lite';
import {
  Route,
  withRouter,
  RouteComponentProps,
  Switch
} from 'react-router-dom';
import HomePage from '../../features/home/HomePage';
import NotFound from './NotFound';
import {ToastContainer} from 'react-toastify';
import { RootStoreContext } from '../stores/rootStore';
import LoadingComponent from './LoadingComponent';
import ModalContainer from '../common/modals/ModalContainer';
import PrivateRoute from './PrivateRoute';
import AffilieDashboard from '../../features/affilies/dashboard/AffilieDashboard';
import AffilieDetails from '../../features/affilies/details/AffilieDetails';

const App: React.FC<RouteComponentProps> = ({ location }) => {
  const rootStore = useContext(RootStoreContext);
  const {setAppLoaded, token, appLoaded} = rootStore.commonStore;
  const {getUser} = rootStore.userStore;

  useEffect(() => {
    if (token) {
      getUser().finally(() => setAppLoaded())
    } else {
      setAppLoaded();
    }
  }, [getUser, setAppLoaded, token])

  if (!appLoaded) return <LoadingComponent content='Chargement app...' />



  return (


    <body id="root">
    <div className="ui container">
      <div className="ui tablet computer only padded grid">
        <div className="ui borderless fluid huge menu">
          <a className="header item">Project Name</a>
          <a className="active item">Home</a> <a className="item">About</a>
          <a className="item">Contact</a>
          <a className="ui dropdown item">
            Dropdown <i className="dropdown icon"></i>
            <div className="menu">
              <div className="item">Action</div>
              <div className="item">Another action</div>
              <div className="item">Something else here</div>
              <div className="ui divider"></div>
              <div className="header">Navbar header</div>
              <div className="item">Seperated link</div>
              <div className="item">One more seperated link</div>
            </div>
          </a>
          <div className="right menu">
            <a className="active item">Default</a> <a className="item">Static top</a>
            <a className="item">Fixed top</a>
          </div>
        </div>
      </div>
      <div className="ui mobile only padded grid">
        <div className="ui borderless huge fluid menu">
          <a className="header item">Project Name</a>
          <div className="right menu">
            <div className="item">
              <button className="ui icon toggle basic button">
                <i className="content icon"></i>
              </button>
            </div>
          </div>
          <div className="ui vertical borderless fluid menu">
            <a className="active item">Home</a> <a className="item">About</a>
            <a className="item">Contact</a>
            <div className="ui dropdown item">
              Dropdown<i className="dropdown icon"></i>
              <div className="menu">
                <a className="item"> Action </a>
                <a className="item"> Another action </a>
                <a className="item"> Something else here </a>
                <div className="ui divider"></div>
                <div className="header">Navbar header</div>
                <a className="item"> Seperated link </a>
                <a className="item"> One more seperated link </a>
              </div>
            </div>
            <a className="active item">Default</a> <a className="item">Static top</a>
            <a className="item">Fixed top</a>
          </div>
        </div>
      </div>
      <div className="ui message">
        <h1 className="ui huge header">Navbar example</h1>
        <p className="lead">
          This example is a quick exercise to illustrate how the default, static
          navbar and fixed to top navbar work. It includes the responsive CSS
          and HTML, so it also adapts to your viewport and device.
        </p>
        <a className="ui big primary button">View navbar docs &raquo; </a>
      </div>
    </div>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/jquery/3.3.1/jquery.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.4.1/semantic.min.js"></script>
    <script src="/Users/abdelilah/Desktop/App/client-app/src/app/layout/script.js"></script>



  </body>



  );
};

export default withRouter(observer(App));


