import React, { useContext, useEffect, useState } from 'react';
import { Grid, Input } from 'semantic-ui-react';
import AffilieList from './AffilieList';
import { observer } from 'mobx-react-lite';
import { RootStoreContext } from '../../../app/stores/rootStore';
import InfiniteScroll from 'react-infinite-scroller';
import AffilieListItemPlaceholder from './AffilieListItemPlaceholder';




const AffilieDashboard: React.FC = () => {
  const rootStore = useContext(RootStoreContext);
  const {
    loadAffilies,
    loadingInitial,
    setPage,
    page,
    totalPages
    , setCin
  } = rootStore.affilieStore;
  const [loadingNext, setLoadingNext] = useState(false);

  const handleGetNext = () => {
    setLoadingNext(true);
    setPage(page + 1);
    loadAffilies().then(() => setLoadingNext(false));
  };


  useEffect(() => {
    loadAffilies();
  }, [loadAffilies]);

  const load = (event: any) => {

    if (event.key === "Enter") {
      setLoadingNext(true);
      setPage(0)
      loadAffilies().then(() => setLoadingNext(false));

      var field = document.createElement('input');
      field.setAttribute('type', 'text');
      document.body.appendChild(field);

      setTimeout(function () {
        field.focus();
        setTimeout(function () {
          field.setAttribute('style', 'display:none;');
        }, 1);
      }, 1);
    }

  }


  return (


    <Grid stackable columns={3}>


      <Grid.Column width={8}>
        {loadingInitial && page === 0 ? (
          <AffilieListItemPlaceholder />
        ) : (
            <InfiniteScroll
              pageStart={0}
              loadMore={handleGetNext}
              hasMore={!loadingNext && page + 1 < totalPages}
              initialLoad={false}
            >
              <AffilieList />

            </InfiniteScroll>

          )}

        {page > 0 ? (
          <AffilieListItemPlaceholder />
        ) : null}




      </Grid.Column>

      <Grid.Column width={4}>


      </Grid.Column>
    </Grid>


















  );
};

export default observer(AffilieDashboard);
