import React, { useContext, useEffect } from 'react';
import { Grid } from 'semantic-ui-react';
import { observer } from 'mobx-react-lite';
import { RouteComponentProps } from 'react-router';
import LoadingComponent from '../../../app/layout/LoadingComponent';
import AffilieDetailedHeader from './AffilieDetailedHeader';
import AffilieDetailedInfo from './AffilieDetailedInfo';
import ActivityDetailedChat from './ActivityDetailedChat';
import { RootStoreContext } from '../../../app/stores/rootStore';

interface DetailParams {
  id: string;
}

const AffilieyDetails: React.FC<RouteComponentProps<DetailParams>> = ({
  match,
  history
}) => {
  const rootStore = useContext(RootStoreContext);
  const { affilie, loadAffilie, loadingInitial } = rootStore.affilieStore;

  useEffect(() => {
    loadAffilie(match.params.id);
  }, [loadAffilie, match.params.id, history]);

  if (loadingInitial) return <LoadingComponent content='Loading Affilies...' />;

  if (!affilie) return <h2>Affilie not found</h2>;

  return (
    <Grid>
      <Grid.Column width={10}>
        <AffilieDetailedHeader affilie={affilie} />
        <AffilieDetailedInfo affilie={affilie} />
      </Grid.Column>
    </Grid>
  );
};

export default observer(AffilieyDetails);
