import React from 'react';
import {Card,Image, Icon, Item} from 'semantic-ui-react';
import {format} from 'date-fns';



import { IAffilie } from '../../../app/models/affilie';
import { Link } from 'react-router-dom';

const AffilieListItem: React.FC<{affilie: IAffilie }> = ({ affilie }) => {
  //const host = affilie.attendees.filter(x => x.isHost)[0];
  return (
    <Card as={Link} to={`/affilies/${affilie.cin}`}>
      {affilie.sexe ==='M' ? (
      <Image src='/assets/Profile-720.png' wrapped ui={false} /> ):
      (<Image src='https://react.semantic-ui.com/images/avatar/large/molly.png' wrapped ui={false} /> )}

    <Card.Content>
    <Card.Header> {affilie.nom} {affilie.prenom}</Card.Header>
    <Item.Description >
                {affilie.cin}
      </Item.Description>
      <Card.Meta>
      {affilie.rest > 0 ? (
        <div>
        <b>  Rest : </b>
        <span className='date' style={{color: "Red" , fontWeight: "bold"}}>{affilie.rest}</span>
          <b>  Reliquat : </b>
         <span className='date' style={{color: "Red" , fontWeight: "bold"}}>{affilie.reliquat}</span>

        </div>

        ) : (
          null
        )}

      </Card.Meta>
      <Card.Description>
      <Icon name='map marker alternate' />
        {affilie.adresse}
      </Card.Description>

      {affilie.telephone  ?(
      <Card.Description>
        <Icon name='phone' />
        {affilie.telephone}
      </Card.Description>):null}
      {affilie.telephone2  ?(
      <Card.Description>
        <Icon name='phone' />
        {affilie.telephone2}
      </Card.Description>):null}

    </Card.Content>
    <Card.Content extra>

    {format(affilie.dateNaissance, 'EEEE d MMMM yyyy')} 

    </Card.Content>

    </Card>


