import React, { Fragment } from 'react';
import { IAffilie } from '../../../app/models/affilie';
import { Document, Page, Text, View, StyleSheet} from '@react-pdf/renderer'
import { observer } from 'mobx-react-lite';
import styled from '@react-pdf/styled-components';

const PdfQp:  React.FC<{affilie: IAffilie }> = ({ affilie }) => {




    const borderColor = '#90e5fc'
    const styles = StyleSheet.create({
        row: {
            flexDirection: 'row',
            borderBottomColor: '#bff0fd',
            borderBottomWidth: 1,
            alignItems: 'center',
            height: 24,
            fontStyle: 'bold',
        },
        description: {
            width: '60%',
            textAlign: 'left',
            borderRightColor: borderColor,
            borderRightWidth: 1,
            paddingLeft: 8,
        },
        qty: {
            width: '10%',
            borderRightColor: borderColor,
            borderRightWidth: 1,
            textAlign: 'right',
            paddingRight: 8,
        },
        rate: {
            width: '15%',
            borderRightColor: borderColor,
            borderRightWidth: 1,
            textAlign: 'right',
            paddingRight: 8,
        },
        amount: {
            width: '15%',
            textAlign: 'right',
            paddingRight: 8,
        },
      });

  const rows =  affilie.Qps.map( item => 
    <View style={styles.row} key={item.nDossier}>
        <Text style={styles.description}>{affilie.prenom}</Text>
        <Text style={styles.qty}>{item.nDossier}</Text>
        <Text style={styles.rate}>{item.rembAmo}</Text>
        <Text style={styles.amount}>{item.rembMpsc }</Text>
    </View>)


  return (

    <Fragment>

{rows}

    </Fragment>


  );


};

export default observer(PdfQp);
