import React from 'react';
import { Card, Image, Segment } from 'semantic-ui-react';
import { format } from 'date-fns';
import local from 'date-fns/locale/fr';
import { IAffilie } from '../../../app/models/affilie';







const MisesAjours: React.FC<{ affilie: IAffilie }> = ({ affilie }) => {

    return (


        <div>

            {affilie.misAjours.map(Maj => Maj.rejetMajs.length > 0 ?

                Maj.rejetMajs.map(rej => rej.corrigeRejets.length > 0 ?
                    (rej.corrigeRejets.map(corri =>
                        <>
                            <Segment vertical>
                                {format(Maj.dateMaj, 'd MMMM yyyy', { locale: local })}
                                {Maj.typeMaj}
                                {Maj.info} {Maj.infoIdentifiant}
                                {format(rej.dateRejet, 'd MMMM yyyy', { locale: local })}
                                {rej.motif}
                                {format(corri.dateCorrige, 'd MMMM yyyy', { locale: local })}
                            </Segment>

                        </>

                    )
                    ) :

                    <>
                        <Image
                            fluid
                            label={{ as: 'a', color: 'red', corner: 'right', icon: 'thumbs down' }}
                        />
                        <Segment vertical>
                            {format(Maj.dateMaj, 'd MMMM yyyy', { locale: local })}
                            {Maj.typeMaj}
                            {Maj.info} {Maj.infoIdentifiant}
                            {format(rej.dateRejet, 'd MMMM yyyy', { locale: local })}
                            {rej.motif}
                        </Segment>


                    </>
                ) :
                <>
                    <Segment vertical>
                        {format(Maj.dateMaj, 'd MMMM yyyy', { locale: local })}
                        {Maj.typeMaj}
                        {Maj.info} {Maj.infoIdentifiant}
                    </Segment>
                </>

            )
            }






        </div>


    );


}

export default MisesAjours



