import React from 'react';
import { IAffilie } from '../../../app/models/affilie';
import { Document, Page, Text, View, StyleSheet,Image} from '@react-pdf/renderer'
import { observer } from 'mobx-react-lite';
import {format} from 'date-fns'
import local from 'date-fns/locale/fr'
import logo from '/assets/logo.png'

const PdfQp:  React.FC<{affilie: IAffilie }> = ({ affilie }) => {


    const styles = StyleSheet.create({
        page: {
            fontFamily: 'Helvetica',
            fontSize: 9,
            paddingTop: 20,
            paddingLeft:10,
            paddingRight:10,
            lineHeight: 1.5,
            flexDirection: 'column',
        }, 
        logo: {
            width: 120,
            height: 66,
            marginLeft: 'auto',
            marginRight: 'auto'
        },
        row: {
            flexDirection: 'row',
            borderBottomColor: 'gray',
            borderBottomWidth: 1,
            alignItems: 'center',
            height: 24,
            fontStyle: 'bold',
        },
        description: {
            flexDirection: 'column',
            width: '14%',
            textAlign: 'center',
            borderRightColor: 'black',
            borderRightWidth: 1,
            paddingLeft:8,
        },
        TableHeader: {
            flexDirection: 'column',
            width: '14%',
            textAlign: 'left',
            borderRightColor: 'black',
            borderRightWidth: 1,
            paddingLeft:8,
            fontStyle: 'bold',
            textTransform: 'uppercase',
        },

        titleContainer:{
            flexDirection: 'row',
            marginTop: 24,
        },
        reportTitle:{
            color: 'black',
            letterSpacing: 2,
            fontSize: 20,
            textAlign: 'center',
            textTransform: 'uppercase',
        }
      });




  return (
    <Document>
        <Page size="A4" style={styles.page}>
        <Image style={styles.logo} src='/assets/logo_.jpg'/>
        <View style={styles.titleContainer}>
        <Text style={styles.reportTitle}>{affilie.nom +' '+ affilie.prenom}</Text>
        </View>
                 <View style={styles.row}>
                 <Text style={styles.TableHeader}>N Dossier</Text>
                 <Text style={styles.TableHeader}>DATE Pai</Text>
                <Text style={styles.TableHeader}>Fr.Engage</Text>
                <Text style={styles.TableHeader}>R.CNOPS</Text>
                <Text style={styles.TableHeader}>R.MPSC</Text>
                <Text style={styles.TableHeader}>Total.R</Text>
                <Text style={styles.TableHeader}>Nature.Dos</Text>

               </View>
            {affilie.Qps.map(qp=> 
               <View style={styles.row}>
                 <Text style={styles.description}>{qp.nDossier}</Text>
                 <Text style={styles.description}>{format(qp.datePaiementReel, 'dd/MM/yyyy', { locale: local })}</Text>
                <Text style={styles.description}>{qp.fraisEngage}</Text>
                <Text style={styles.description}>{qp.rembAmo} </Text>
                <Text style={styles.description}>{qp.rembMpsc} </Text>
                <Text style={styles.description}>{qp.totalRemb} </Text>
                <Text style={styles.description}>{qp.observation} </Text>

               </View>
                )}

        </Page>
      </Document>
  );


};

export default observer(PdfQp);
