import React from 'react';
import { IAffilie } from '../../../app/models/affilie';
import { Document, Page, Text, View, StyleSheet} from '@react-pdf/renderer'
import { observer } from 'mobx-react-lite';
import {format} from 'date-fns'
import local from 'date-fns/locale/fr'

const PdfQp:  React.FC<{affilie: IAffilie }> = ({ affilie }) => {


    const styles = StyleSheet.create({
        page: {
            fontFamily: 'Helvetica',
            fontSize: 11,
            paddingTop: 30,
            paddingLeft:60,
            paddingRight:60,
            lineHeight: 1.5,
            flexDirection: 'column',
        }, 
        logo: {
            width: 74,
            height: 66,
            marginLeft: 'auto',
            marginRight: 'auto'
        },
        row: {
            flexDirection: 'row',
            borderBottomColor: '#bff0fd',
            borderBottomWidth: 1,
            alignItems: 'center',
            height: 24,
            fontStyle: 'bold',
        },
        description: {
            width: '60%',
            textAlign: 'left',
            borderRightColor: '#bff0fd',
            borderRightWidth: 1,
            paddingLeft: 8,
        }
      });




  return (
    <Document>
        <Page size="A4" style={styles.page}>
            {affilie.Qps.map(qp=> 
               <View style={styles.row}>
                <Text style={styles.description}>{qp.rembMpsc} Dhs </Text>
                <Text style={styles.description}>{format(qp.datePaiementReel, 'EEEE d MMMM yyyy', { locale: local })}</Text>
               </View>
                )}

          <View style={styles.section}>
            <Text>Section #2</Text>
          </View>
        </Page>
      </Document>
  );


};

export default observer(PdfQp);
